import '../css/schedule.css';
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css";
import Modal from "react-modal"; 
import React, { useState } from "react";
import DatePicker from "react-date-picker";

const locales = {
    "en-US": require("date-fns/locale/en-US")
}
const localizer = dateFnsLocalizer({
    format,
    parse,
    startOfWeek,
    getDay,
    locales
})

const events = [
  {
       title: "Financial Literacy 101",
     allDay: true,
    start: new Date(2023,8,0),
   end : new Date(2023,8,0)
     }
]

const Mainschedule = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectedEvent, setSelectedEvent] = useState(null);

    const [newEvent, setNewEvent] = useState({ title: "", start: "", end: "" });
    const [allEvents, setAllEvents] = useState(events);

    function handleAddEvent() {
        setAllEvents([...allEvents, newEvent]);
    }

    function handleEventClick(event) {
        setSelectedEvent(event);
        setIsModalOpen(true);
    }

    return (
        <div>
            <div className={`calendar-container ${isModalOpen ? "hide-content" : ""}`}>
                <Calendar 
                    localizer={localizer} 
                    events={allEvents} 
                    startAccessor="start" 
                    endAccessor="end" 
                    style={{ height: 500, margin: "50px" }} 
                    onSelectEvent={handleEventClick}
                />
            </div>

            <Modal
                isOpen={isModalOpen}
                onRequestClose={() => setIsModalOpen(false)}
                contentLabel="Event Details"
            >
                {selectedEvent && (
                    <div>
                        <h2>{selectedEvent.title}</h2>
                        <p>Start: {selectedEvent.start.toString()}</p>
                        <p>End: {selectedEvent.end.toString()}</p>
                        {/* You can add more event details here */}
                    </div>
                )}
            </Modal>
        </div>
    )
}

export default Mainschedule;
