// css
import '../css/schedule.css';

// from react-big-calendar package
import { Calendar, dateFnsLocalizer} from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css"
import Modal from "react-modal"; 
import React, { useState } from "react";
import DatePicker from "react-date-picker";

// icons
import { FaCheckCircle } from "react-icons/fa";


const locales = {
    "en-US": require("date-fns/locale/en-US")
}
const localizer = dateFnsLocalizer ({
    format,
    parse,
    startOfWeek,
    getDay,
    locales
})

const events = [
    {
        title: "Financial Literacy 101",

        allDay: true,
        start: new Date(2023,8,0),
        end : new Date(2023,8,0),
        description : "This interactive class empowers participants with essential financial skills. Covering topics like budgeting, saving, investing, and debt management, learners gain practical insights to make informed financial decisions. Engaging discussions, real-world scenarios, and expert insights form the foundation of this course. Navigate personal finances with confidence, build a strong financial future, and achieve goals through actionable strategies. Whether you're new to finance or seeking to refine your skills, this class equips you with the knowledge and tools needed to thrive in a complex financial landscape. Unlock financial freedom and seize control of your economic well-being."

    },
    {
        title: "Affordable Housing Essentials",
        start: new Date(2023,8,2),
        end : new Date(2023,8,2),
        description : "Delve into the dynamics of accessible housing in our comprehensive class. Explore housing policies, market trends, and socio-economic factors influencing affordability. Gain insights into government initiatives, urban planning, and innovative solutions that address housing challenges. Through case studies and discussions, understand the significance of stable housing for communities and individuals. Acquire tools to advocate for affordable housing, assess housing options, and navigate housing programs. Whether you're an advocate, policy maker, or seeking affordable housing, this course equips you with the knowledge to promote equitable housing opportunities and make informed decisions. Empower change and contribute to housing accessibility."
    },
    {
        title: "Mastering Investing Strategies",

        start: new Date(2023,8,1),
        end : new Date(2023, 8, 1),
        description : "Unveil the art and science of investing in this comprehensive course. Dive into fundamental concepts like stocks, bonds, mutual funds, and diversification. Explore advanced topics including portfolio management, risk assessment, and market analysis. Through hands-on simulations, real-time market insights, and expert guidance, participants develop a solid foundation in investment strategies. Whether you're a novice or experienced investor, learn to identify opportunities, manage risks, and optimize returns. From building personal wealth to achieving financial goals, this class empowers you to navigate the complex world of investments with confidence and make informed decisions. Unlock your potential in the global financial arena."
    }
]




const Mainschedule = () => {

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);

  const [newEvent, setNewEvent] = useState({title: "", start: "", end: "", description: "" })
  const [allEvents, setAllEvents] = useState(events)

  function handleAddEvent() {
    setAllEvents([...allEvents, newEvent])
  }

  function handleEventClick(event) {
    setSelectedEvent(event);
    setIsModalOpen(true);

  }


  return (


    <div>
      <div className={isModalOpen ? "hide-dates": ""}>
      <div className={isModalOpen ? "hide-calendar-content": ""}>
      <Calendar 

      localizer={localizer} 
      events={allEvents} 
      startAccessor="start" 
      endAccessor="end" 
      style={{height: 500, margin: "50px"}} 
      onSelectEvent={handleEventClick} // Call the handleEventClick function on event click

      />
      </div>
      </div>


          <Modal
                isOpen={isModalOpen}
                onRequestClose={() => setIsModalOpen(false)}
                contentLabel="Event Details"
                style={{
                  content: {
                      width: "400px", // Adjust the width as needed
                      maxHeight: "70%", // Set maximum height
                      margin: "auto", // Center the modal
                  }
              }}
            >
                {selectedEvent && (
                    <div>
                        <h2>{selectedEvent.title}</h2>
                        <p>Start: {selectedEvent.start.toString()}</p>
                        <p>End: {selectedEvent.end.toString()}</p>
                        <h2>Course Description</h2>
                        <p>{selectedEvent.description.toString()}</p>

                        <button>Add Class</button>

                        <p> <FaGift className='icon' /> Donation </p> 
                        <h2>You Are Signed Up for: {selectedEvent.title}!</h2>


                        <p></p>

                    </div>
                )}
            </Modal>


    </div>


  )
}


export default Mainschedule;
